package day02_20211005;

public class Ex6_If_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 10;
		
		//if를 쓴 뒤 else, else if 를 안쓰고 if를 쓰고 끝낼 경우 둘다 실행된다.
		
		if(num==10) {
			System.out.println("num이 10입니다.");
		} if(num>=0) {
			System.out.println("num이 0보다 크거나 같습니다.");
		}
	}

}
